<?php	function um_change_post_link( $url, $post, $leavename ) {		$new_url = get_post_meta($post->ID,"um_custom_post_url",true);		$post_types_active = get_option("um_post_type_url_changer_post_types");		if($new_url && in_array($post->post_type,$post_types_active)){			return $new_url;		}		return $url;	}	add_filter( 'post_type_link', 'um_change_post_link', 10, 3 );	add_filter( 'post_link', 'um_change_post_link', 10, 3 );	add_filter( 'page_link', 'um_change_post_link', 10, 3 );?>